import emailjs from "@emailjs/browser";
import { useRef } from "react";

const Footer = () => {
  const form = useRef();

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_mrs2gmr",
        "template_1atvw44",
        form.current,
        "2vAafyWokU5vCYmE9"
      )
      .then(
        (result) => {
          console.log(result.text);
          console.log("set successssfulllyyyyy");
        },
        (error) => {
          console.log(error.text);
        }
      );
  };
  return (
    <div className="footer">
      <div className="footer-childs">
        <h1>Nova Cars</h1>
        <p>
          We offers a big range of vehicles for all your driving needs. We have
          the perfect car to meet your needs.
        </p>
        <div className="info-cont">
          <i class="fa-solid fa-square-phone"></i>
          <p>(123) -456-789</p>
        </div>
        <div className="info-cont">
          <i class="fa-regular fa-envelope"></i>
          <p>Nova@gmail.com</p>
        </div>

        <div className="social-cont">
          <i class="fa-brands fa-facebook"></i>
          <i class="fa-brands fa-twitter"></i>
          <i class="fa-brands fa-telegram"></i>
          <i class="fa-brands fa-youtube"></i>
        </div>
      </div>
      <div className="footer-childs">
        <h1>COMPANY</h1>
        <a href="#/">New York</a>
        <a href="#/">Brand</a>
        <a href="#/">car dealers</a>
        <a href="#/">car reviews</a>
        <a href="#/">how we work</a>
      </div>
      <div className="footer-childs">
        <h1>WORKING HOURS</h1>
        <p>Mon - Fri: 9:00AM - 9:00PM</p>
        <p>Sat: 9:00AM - 19:00PM</p>
        <p>Sun: Closed</p>
      </div>
      <div className="footer-childs">
        <h1>SUBSCRIPTION</h1>
        <p>Subscribe your Email address for latest news & updates.</p>
        <form className="footer-form" ref={form} onSubmit={sendEmail}>
          <input
            type="email"
            placeholder="Enter Email Address"
            name="user_email"
          />
          <input type="submit" value="Send" />
        </form>
      </div>
      <p className="copy">Â©2023, Nova. All rights reserved.</p>
    </div>
  );
};

export default Footer;
